import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';

function CreateArea(props) {

  const [isExpanded, setExpanded] = useState(false)
  const [errMsg, setErrMsg] = useState("")
  const [noteContent, setNoteContent] = useState({
    title: "",
    content: ""
  });

  function handleChange(event) {
    let { name, value } = event.target;
    setNoteContent(prevValues => {
      return {
        ...prevValues,
        [name]: value
      };
    });
  }

  return (
    <div >
      <form
        onSubmit={event => {
          event.preventDefault();
          if (noteContent.title.trim() !== "" && noteContent.content.trim() !== "") {
            props.onAdd(noteContent);
            setNoteContent({
              title: "",
              content: ""
            });
          }
          else {
            setErrMsg("Empty Title or Content area.")
          }
        }
        }
      >
        <input
          onClick={() => { setExpanded(true); }}
          onDoubleClick = {() => { setExpanded(false); }}
          onChange={handleChange}
          name="title"
          placeholder="Title"
          value={noteContent.title}
        />
        {isExpanded && <textarea

          onChange={handleChange}
          name="content"
          placeholder="Take a note..."
          rows="3"
          value={noteContent.content}
        />}
        <Zoom in={isExpanded}>
          <Fab className="butClass" type="submit">
            <AddIcon onClick={() => {
              if(errMsg){setExpanded(false)}
            }} />
          </Fab>
        </Zoom>
        {errMsg && <div style={{ color: 'red' }}>{errMsg}</div>}
      </form>
    </div>
  );
}

export default CreateArea;
